package Assignments;

public class Swaping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int a=5, b=8, temp;
	System.out.println("a= "+a+"\nb= "+b);
	
	System.out.println("*********After Swapping***********");
	temp= a;
	a=b;
	b=temp;
	System.out.println("a= "+a+"\nb= "+b);
	}
	
	

}
